function catFilters(){$(".categories-more").on("click",function(){$(".toggle-text, .categories-options").toggleClass("hidden")}),$(".categories-item:not(.categories-all)").on("click",function(){$(this).toggleClass("active"),$(".categories-all").removeClass("active")});var e=0;$(".categories-item").each(function(){$(this).hasClass("active")&&(e+=1)}),$(".categories-item").length==e&&$(this).toggleClass("active")}function openPopup(e){$("#popup-"+e).show()}function closePopup(e){$("#popup-"+e).hide()}$(document).ready(function(){catFilters()});